mapper.insert:
INSERT INTO SHOP_ITEM (
    ITEM_CODE
    , ITEM_NAME
    , ITEM_PRICE
    , ITEM_INTRO
    , CATE_CODE
) VALUES (
    (SELECT 'ITEM_'|| LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ITEM_CODE, 6))), 0) + 1, 3 ,'0')
        FROM SHOP_ITEM)
    , #{itemName}
    , #{itemPrice}
    , #{itemIntro}
    , #{cateCode}
)

mapper.update:
UPDATE SHOP_ITEM
SET ITEM_STOCK = #{itemStock}
WHERE ITEM_CODE = #{itemCode}
